import React from "react";

const List = ({
  id,
  title,
  completed,
  todoData,
  setTodoData,
  provided,
  snapshot,
}) => {
    console.log('App is rendering');

  const handleClick = (id) => {
    let newTodoData = todoData.filter((data) => data.id !== id);
    setTodoData(newTodoData);
  };

  const handleCompleteChange = (id) => {
    let newTodoData = todoData.map((data) => {
      if (data.id === id) {
        data.completed = !data.completed;
      }
      return data;
    });
    setTodoData(newTodoData);
  };

  return (
    <div
      key={id}
      {...provided.draggableProps}
      ref={provided.innerRef}
      {...provided.dragHandleProps}
      className={`${snapshot.isDragging ? "bg-gray-400" : "bg-gray-100"} flex items-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}>
      <div className="items-center">
        <input
          type="checkbox"
          onChange={() => handleCompleteChange(id)}
          defaultChecked={completed}
        />
        <span className={completed ? "line-through" : undefined}>{title}</span>
        <div className="items-center">
          <button className="float-right px-4 py-2" onClick={() => handleClick(id)}>x</button>
        </div>
      </div>
    </div>
  );
};

export default List;
